import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { ethers } from 'ethers';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Header from './components/Header';
import Dashboard from './components/Dashboard';
import Voting from './components/Voting';
import Admin from './components/Admin';
import { VotingContract } from './contracts/VotingContract';

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [contract, setContract] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    initializeApp();
  }, []);

  const initializeApp = async () => {
    try {
      // Check if MetaMask is installed
      if (typeof window.ethereum !== 'undefined') {
        const provider = new ethers.BrowserProvider(window.ethereum);
        setProvider(provider);

        // Get connected accounts
        const accounts = await provider.listAccounts();
        if (accounts.length > 0) {
          await connectWallet(accounts[0]);
        }

        // Listen for account changes
        window.ethereum.on('accountsChanged', async (accounts) => {
          if (accounts.length > 0) {
            await connectWallet(accounts[0]);
          } else {
            setAccount(null);
            setContract(null);
            setIsAdmin(false);
          }
        });
      } else {
        toast.error('Please install MetaMask to use this dApp');
      }
    } catch (error) {
      console.error('Error initializing app:', error);
      toast.error('Failed to initialize the application');
    } finally {
      setLoading(false);
    }
  };

  const connectWallet = async (accountAddress) => {
    try {
      setAccount(accountAddress);

      // Initialize contract
      const signer = await provider.getSigner();
      const votingContract = new VotingContract(signer);
      setContract(votingContract);

      // Check if user is admin
      const owner = await votingContract.getOwner();
      setIsAdmin(owner.toLowerCase() === accountAddress.toLowerCase());

      toast.success('Wallet connected successfully!');
    } catch (error) {
      console.error('Error connecting wallet:', error);
      toast.error('Failed to connect wallet');
    }
  };

  const connectMetaMask = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        const accounts = await window.ethereum.request({
          method: 'eth_requestAccounts'
        });
        await connectWallet(accounts[0]);
      } else {
        toast.error('Please install MetaMask');
      }
    } catch (error) {
      console.error('Error connecting MetaMask:', error);
      toast.error('Failed to connect MetaMask');
    }
  };

  const disconnectWallet = () => {
    setAccount(null);
    setContract(null);
    setIsAdmin(false);
    toast.info('Wallet disconnected');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-white text-2xl">Loading USA 2028 Voting dApp...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <Header 
        account={account} 
        isAdmin={isAdmin}
        onConnect={connectMetaMask}
        onDisconnect={disconnectWallet}
      />

      <main className="container mx-auto px-4 py-8">
        <Routes>
          <Route 
            path="/" 
            element={
              <Dashboard 
                contract={contract}
                account={account}
                isAdmin={isAdmin}
              />
            } 
          />
          <Route 
            path="/vote" 
            element={
              <Voting 
                contract={contract}
                account={account}
              />
            } 
          />
          <Route 
            path="/admin" 
            element={
              <Admin 
                contract={contract}
                account={account}
                isAdmin={isAdmin}
              />
            } 
          />
        </Routes>
      </main>

      <ToastContainer 
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </div>
  );
}

export default App;
